{-# OPTIONS -fno-warn-incomplete-patterns #-}
module Printjson where

-- pretty-printer generated by the BNF converter

import Absjson
import Char

-- the top-level printing method
printTree :: Print a => a -> String
printTree = render . prt 0

type Doc = [ShowS] -> [ShowS]

doc :: ShowS -> Doc
doc = (:)

render :: Doc -> String
render d = rend 0 (map ($ "") $ d []) "" where
  rend i ss = case ss of
    "["      :ts -> showChar '[' . rend i ts
    "("      :ts -> showChar '(' . rend i ts
    "{"      :ts -> showChar '{' . new (i+1) . rend (i+1) ts
    "}" : ";":ts -> new (i-1) . space "}" . showChar ';' . new (i-1) . rend (i-1) ts
    "}"      :ts -> new (i-1) . showChar '}' . new (i-1) . rend (i-1) ts
    ";"      :ts -> showChar ';' . new i . rend i ts
    t  : "," :ts -> showString t . space "," . rend i ts
    t  : ")" :ts -> showString t . showChar ')' . rend i ts
    t  : "]" :ts -> showString t . showChar ']' . rend i ts
    t        :ts -> space t . rend i ts
    _            -> id
  new i   = showChar '\n' . replicateS (2*i) (showChar ' ') . dropWhile isSpace
  space t = showString t . (\s -> if null s then "" else (' ':s))

parenth :: Doc -> Doc
parenth ss = doc (showChar '(') . ss . doc (showChar ')')

concatS :: [ShowS] -> ShowS
concatS = foldr (.) id

concatD :: [Doc] -> Doc
concatD = foldr (.) id

replicateS :: Int -> ShowS -> ShowS
replicateS n f = concatS (replicate n f)

-- the printer class does the job
class Print a where
  prt :: Int -> a -> Doc
  prtList :: [a] -> Doc
  prtList = concatD . map (prt 0)

instance Print a => Print [a] where
  prt _ = prtList

instance Print Char where
  prt _ s = doc (showChar '\'' . mkEsc '\'' s . showChar '\'')
  prtList s = doc (showChar '"' . concatS (map (mkEsc '"') s) . showChar '"')

mkEsc :: Char -> Char -> ShowS
mkEsc q s = case s of
  _ | s == q -> showChar '\\' . showChar s
  '\\'-> showString "\\\\"
  '\n' -> showString "\\n"
  '\t' -> showString "\\t"
  _ -> showChar s

prPrec :: Int -> Int -> Doc -> Doc
prPrec i j = if j<i then parenth else id


instance Print Integer where
  prt _ x = doc (shows x)


instance Print Double where
  prt _ x = doc (shows x)




instance Print JSONObject where
  prt i e = case e of
   JObject jsonpairs -> prPrec i 0 (concatD [doc (showString "{") , prt 0 jsonpairs , doc (showString "}")])


instance Print JSONPair where
  prt i e = case e of
   JPair str jsonvalue -> prPrec i 0 (concatD [prt 0 str , doc (showString ":") , prt 0 jsonvalue])

  prtList es = case es of
   [] -> (concatD [])
   [x] -> (concatD [prt 0 x])
   x:xs -> (concatD [prt 0 x , doc (showString ",") , prt 0 xs])

instance Print JSONArray where
  prt i e = case e of
   JArray jsonvalues -> prPrec i 0 (concatD [doc (showString "[") , prt 0 jsonvalues , doc (showString "]")])


instance Print JSONValue where
  prt i e = case e of
   JStr str -> prPrec i 0 (concatD [prt 0 str])
   JNum d -> prPrec i 0 (concatD [prt 0 d])
   JObj jsonobject -> prPrec i 0 (concatD [prt 0 jsonobject])
   JArr jsonarray -> prPrec i 0 (concatD [prt 0 jsonarray])
   JTru  -> prPrec i 0 (concatD [doc (showString "true")])
   JFal  -> prPrec i 0 (concatD [doc (showString "false")])
   JNul  -> prPrec i 0 (concatD [doc (showString "null")])

  prtList es = case es of
   [] -> (concatD [])
   [x] -> (concatD [prt 0 x])
   x:xs -> (concatD [prt 0 x , doc (showString ",") , prt 0 xs])


